pragma solidity ^0.4.18;

import "./CVExtender.sol";
import "./mortal.sol";

contract CV is CVExtender,mortal {
    uint256 projectCounter;
    uint256 recCounter;
    struct project{
        string _heading;
        string _description;
        string _url;
    }
    struct recommendation{
        string _rec;
        string _giver;
        string _contact;
    }

    mapping (uint256 => project) my_projects;
    mapping (uint256 => recommendation) my_recommendations;

    event projectAdded(uint256 projectCtr, string heading, string desc, string url,string msg);
    event recommendationReceieved(uint256 recCtr,address indexed sender,string rec, string auth, string contact);
    event recommendationDeleted(uint256 recCtr,string giver);


    function addProject(string heading, string description, string URL) onlyowner public{
        if(owner==msg.sender){
                projectCounter+=1;
                my_projects[projectCounter]=project(heading,description,URL);
                projectAdded(projectCounter,heading,description,URL,"D");
            }
          
            
        }  
    
    function WriteRecommendation(string rec, string auth, string contact) public{
       if(owner!=msg.sender){
                recCounter+=1;
                my_recommendations[recCounter]=recommendation(rec,auth,contact);
                recommendationReceieved(recCounter,msg.sender,rec,auth,contact);
            }
            
        } 
     function deleteRecommendation(uint256 recCtr) public{
       if(owner==msg.sender){
           if(recCtr<=recCounter){
               string giv=my_recommendations[recCtr]._giver;
              
           recommendationDeleted(recCtr,giv);    
          
        
        
           }
        }
            
        } 
   
   
    function getAddress() public view returns(string) {
        return "http://www.urjapawar.in";
    }

    function getDescription() public view returns(string) {
        return "Here is my CV for consideration in the Blockchain domain";
    }
    function getTitle() public view returns(string) {
        return "My CV";
    }
    function getAuthor() public view returns(string, string) {
        return ("Urja Pawar", "urjapawar@gmail.com");
    }
}
